import React from "react";
import PortfolioHero from "./components/PortfolioHero";
import AboutMe from "./components/AboutMe";
import { Link } from "react-scroll";

function App() {
  return (
    <div>
      {/* Simple Navigation at the top */}
      <nav className="flex justify-center gap-8 py-6 bg-black text-white fixed top-0 w-full z-50">
        <Link
          to="about"
          smooth={true}
          duration={2000}
          offset={-50}
          className="cursor-pointer hover:text-pink-400"
        >
          About
        </Link>
        {/* Add more links here if needed */}
      </nav>

      <div className="pt-20"> {/* Push content below the fixed nav */}
        <PortfolioHero />
        <AboutMe />
      </div>
    </div>
  );
}

export default App;
