import React from "react";
import PortfolioHero from "./components/PortfolioHero";
import AboutMe from "./components/AboutMe";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import { Link } from "react-scroll";

function App() {
  return (
    <div>
      {/* Sticky Navigation Bar */}
      <nav className="flex justify-center gap-8 py-6 bg-black text-white fixed top-0 w-full z-50 shadow-lg">
        <Link
          to="about"
          smooth={true}
          duration={2000}
          offset={-50}
          className="cursor-pointer hover:text-pink-400 transition-colors"
        >
          About
        </Link>
        <Link
          to="projects"
          smooth={true}
          duration={2000}
          offset={-50}
          className="cursor-pointer hover:text-pink-400 transition-colors"
        >
          Projects
        </Link>
        <Link
          to="contact"
          smooth={true}
          duration={2000}
          offset={-50}
          className="cursor-pointer hover:text-pink-400 transition-colors"
        >
          Contact
        </Link>
      </nav>

      {/* Page Sections */}
      <div className="pt-24"> {/* Offset for fixed nav */}
        <PortfolioHero />
        <AboutMe />
        <Projects />
        <Contact />
      </div>
    </div>
  );
}

export default App;
