import { motion } from "framer-motion";

export default function AboutMe() {
  return (
    <motion.section
      id="about"
      className="min-h-screen flex items-center justify-center px-6 py-20 bg-black text-white"
      initial={{ opacity: 0, y: 50 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 1 }}
      viewport={{ once: true }}
    >
      <div className="max-w-3xl text-center">
        <h2 className="text-5xl font-bold mb-8 text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-pink-500">
          About Me
        </h2>
        <p className="text-lg text-gray-300 leading-relaxed">
          Hello! I'm Srinivasan Balaji, a full-stack developer who builds powerful web apps.
          I work with React, Node, and Tailwind CSS and love making things look and work great.
          I'm passionate about solving real-world problems through clean code and creative ideas.
        </p>
      </div>
    </motion.section>
  );
}
