import React from "react";
import { motion } from "framer-motion";

export default function AboutMe() {
  return (
    <motion.section
      id="about" // ðŸ‘ˆ This connects with react-scroll's "to='about'"
      className="min-h-screen flex items-center justify-center px-6 py-20 bg-black text-white"
      initial={{ opacity: 0, y: 60 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 1, ease: "easeOut" }}
      viewport={{ once: true }}
    >
      <div className="max-w-3xl text-center">
        <h2 className="text-5xl font-bold mb-8 text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-pink-500 animate-color-cycle drop-shadow">
          About Me
        </h2>

        <p className="text-lg text-gray-300 leading-relaxed mb-6">
          Hello stranger! My name is <strong>B. Srinivasan</strong>, a full-stack developer and data analyst with a love for solving real-world problems through code.
        </p>

        <p className="text-lg text-gray-300 leading-relaxed mb-6">
          I specialize in React.js, Node.js, Tailwind CSS, and MongoDB. I create high-performance applications that are both functional and beautiful.
        </p>

        <p className="text-lg text-gray-300 leading-relaxed mb-6">
          My strengths include building smooth UIs, implementing smart logic, and using tools like Framer Motion for animations and Google APIs for dynamic experiences.
        </p>

        <p className="text-lg text-gray-300 leading-relaxed mb-8">
          Outside of development, I enjoy design, creativity, and pushing the limits of whatâ€™s possible on the web.
        </p>

        <p className="text-base text-center">
          Check out my{" "}
          <a
            href="/resume.pdf"
            className="underline text-blue-400 hover:text-blue-500"
            download
          >
            resume
          </a>{" "}
          for more details.
        </p>
      </div>
    </motion.section>
  );
}
