import React from "react";
import PortfolioHero from "./components/PortfolioHero";
import AboutMe from "./components/AboutMe";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import SkillsSection from "./components/SkillsSection"; // ðŸ‘ˆ Add this line
import { Link } from "react-scroll";

function App() {
  return (
    <div>
      <div className="fixed top-0 left-0 w-full h-1 bg-gradient-to-r from-sky-400 via-green-400 to-blue-500 z-[999]" />
      {/* Sticky Navigation Bar */}
      <nav className="flex justify-center gap-8 py-6 bg-black text-white fixed top-0 w-full z-50 shadow-lg">
        <Link
          to="about"
          smooth={true}
          duration={2000}
          offset={-50}
          className="cursor-pointer hover:text-pink-400 transition-colors"
        >
          About
        </Link>
        <Link
          to="skills"
          smooth={true}
          duration={2000}
          offset={-50}
          className="cursor-pointer hover:text-pink-400 transition-colors"
        >
          What I Do
        </Link>
        <Link
          to="projects"
          smooth={true}
          duration={2000}
          offset={-50}
          className="cursor-pointer hover:text-pink-400 transition-colors"
        >
          Projects
        </Link>
        <Link
          to="contact"
          smooth={true}
          duration={2000}
          offset={-50}
          className="cursor-pointer hover:text-pink-400 transition-colors"
        >
          Contact
        </Link>
      </nav>

      {/* Page Sections */}
      <div className="w-full h-1 bg-gradient-to-r from-blue-500 via-violet-500 to-indigo-500 animate-pulse" />
      <div className="pt-16"></div>
      <div className="pt-24">
        <PortfolioHero />
        <AboutMe />
        <SkillsSection /> {/* ðŸ‘ˆ New Skills Section Here */}
        <Projects />
        <Contact />
      </div>
    </div>
  );
}

export default App;
