import { useEffect } from "react";
import { Github, Linkedin, FileText, Instagram } from "lucide-react";
import { motion } from "framer-motion";

export default function PortfolioHero() {
  useEffect(() => {
    document.title = "B. Srinivasan | Portfolio";
  }, []);

  return (
    <div className="min-h-screen bg-black text-white flex flex-col justify-center items-center px-4">
      <nav className="absolute top-4 right-4 flex gap-4 text-sm font-semibold">
        <a href="/resume.pdf" className="hover:underline">RESUME</a>
        <a href="#" className="hover:underline">MENU</a>
      </nav>

      <motion.div
        className="text-center"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
       <h1 className="text-4xl md:text-6xl font-extrabold leading-tight text-white text-center">
  Hi, I'm <span className="font-bold">
     <span className="animate-color-cycle">B.Srinivasan</span>
    </span><br />
    <span className="text-white">
    Solutions born in logic, <br className="hidden md:block" />
    shaped by design.
  </span>
</h1>



        <p className="mt-6 text-sm uppercase tracking-widest">Explore Works</p>
        import { Link } from "react-scroll";

<Link
  to="projects"
  smooth={true}
  duration={1500}
  offset={-50}
>
  <button className="px-6 py-3 bg-pink-500 text-white rounded-lg hover:bg-pink-600 transition-all">
    View My Projects
  </button>
</Link>

      </motion.div>

      <footer className="absolute bottom-4 flex gap-6 text-white text-sm">
        <a href="https://github.com/BSrinivasan007" target="_blank" rel="noopener noreferrer"><Github /></a>
        <a href="https://www.linkedin.com/in/srinivasan-balaji-64b3262b8/" target="_blank" rel="noopener noreferrer"><Linkedin /></a>
        <a href="https://www.instagram.com/__.s.r.i.n.i.v.a.s.a.n.__/?next=%2F" target="_blank"><Instagram /></a>
        <a href="/resume.pdf" target="_blank" rel="noopener noreferrer"><FileText /></a>
      </footer>
    </div>
  );
}
