import { motion, useScroll, useTransform } from "framer-motion";
import { useRef } from "react";

export default function AboutMe() {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start end", "end start"],
  });

  const y = useTransform(scrollYProgress, [0, 1], ["20%", "0%"]);
  const opacity = useTransform(scrollYProgress, [0, 1], [0, 1]);

  return (
    <motion.section
      ref={ref}
      style={{ y, opacity }}
      className="bg-black text-white px-4 py-24 flex justify-center"
    >
      <div className="max-w-4xl w-full text-center">
        <h2 className="text-5xl font-extrabold mb-8 bg-gradient-to-r from-purple-400 to-pink-400 text-transparent bg-clip-text">
          About Me
        </h2>
        <div className="text-lg leading-8 text-gray-300 text-left md:text-center">
          <p className="mb-6">
            Hello stranger! My name is <strong>B. Srinivasan</strong> and I build full-stack web projects that solve real problems...
          </p>
          <p className="mt-8 text-center">
            Check out my <a href="/resume.pdf" className="underline text-blue-400 hover:text-blue-500">resume</a> to know more.
          </p>
        </div>
      </div>
    </motion.section>
  );
}
