import React from "react";
import { motion } from "framer-motion";


export default function Experience() {
  return (
    <section id="experience" className="bg-black text-white py-20 px-6">
      <div className="max-w-5xl mx-auto">
        <h2 className="text-4xl font-extrabold mb-8 text-center text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-blue-500">
          Experience
        </h2>

        <div className="space-y-8">
          {experiences.map((exp, index) => (
            <motion.div
              key={index}
              className="border-l-4 border-blue-400 pl-6 relative"
              initial={{ opacity: 0, x: -40 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: index * 0.2 }}
              viewport={{ once: true }}
            >
              <h3 className="text-2xl font-semibold text-white">{exp.role}</h3>
              <p className="text-blue-400 font-medium">{exp.company}</p>
              <p className="text-sm text-gray-400 mb-2">{exp.duration}</p>
              <p className="text-gray-300 leading-relaxed">{exp.details}</p>
            </motion.div>
          ))}
        </div>

        {/* ðŸ“© Add Copy Email Button */}
        <div className="mt-12 text-center">
          <button
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded transition"
            onClick={() => {
              navigator.clipboard.writeText("kbcsrinivasan@gmail.com");
              alert("Email copied to clipboard!");
            }}
          >
            Copy Email Address
          </button>
        </div>
      </div>
    </section>
  );
}
