import React from "react";
import { motion } from "framer-motion";

const projects = [
  {
    title: "Chatty App",
    tech: "React, Node.js, MongoDB",
    desc: "A real-time chat app with user authentication and instant messaging.",
  },
  {
    title: "PDF Analyzer",
    tech: "Python, Streamlit, Google API",
    desc: "RAG-based app to upload a PDF and query its contents using AI.",
  },
  {
    title: "Image Generator",
    tech: "Python, HuggingFace, Streamlit",
    desc: "A UI tool for generating AI-powered images with text prompts.",
  },
];

export default function Projects() {
  return (
    <motion.section
      id="projects"
      className="bg-black text-white py-24 px-6 flex flex-col items-center"
      initial={{ opacity: 0, y: 60 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 1 }}
      viewport={{ once: true }}
    >
      <h2 className="text-5xl font-bold mb-12 text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-pink-500">
        Projects
      </h2>

      <div className="grid md:grid-cols-2 gap-10 max-w-5xl w-full">
        {projects.map((project, index) => (
          <div
            key={index}
            className="border border-gray-700 p-6 rounded-lg bg-gradient-to-br from-gray-900 to-gray-800 shadow-lg"
          >
            <h3 className="text-xl font-bold text-blue-300 mb-2">
              {project.title}
            </h3>
            <p className="text-gray-400 text-sm mb-2">{project.tech}</p>
            <p className="text-gray-300">{project.desc}</p>
          </div>
        ))}
      </div>
    </motion.section>
  );
}
